// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";

/**
 * @title Chainlink Price Feed Aggregator V3 Mock
 *
 * @notice Supports the Illuvitars Price Oracle with the ILV/ETH price feed
 *
 * @dev Enables testing of the feed, playing with current timestamp
 *
 * @author Basil Gorin
 */
contract ChainlinkAggregatorV3Mock is AggregatorV3Interface {
    // values returned by `latestRoundData()`
    uint80 public roundIdMocked = 1;
    int256 public answerMocked = -1;
    uint256 public startedAtMocked = type(uint256).max;
    uint256 public updatedAtMocked = type(uint256).max;
    uint80 public answeredInRoundMocked = 1;
    // answer (conversion rate) is derived from the ILV/ETH ratio
    // initial conversion rate is 1 ETH = 4 ILV
    uint256 public ethOut = 1;
    uint256 public ilvIn = 4;

    /// @dev overridden value to use as now32()
    uint256 private _now256;

    /// @dev overrides now256()
    function setNow256(uint256 value) public {
        _now256 = value;
    }

    /**
     * @dev Testing time-dependent functionality may be difficult;
     *      we override time in the helper test smart contract (mock)
     *
     * @return `block.timestamp` in mainnet, custom values in testnets (if overridden)
     */
    function now256() public view returns (uint256) {
        return _now256 > 0 ? _now256 : block.timestamp;
    }

    /**
     * @dev Overrides roundId, answer, startedAt, updatedAt, answeredInRound
     */
    function setMockedValues(
        uint80 roundId,
        int256 answer,
        uint256 startedAt,
        uint256 updatedAt,
        uint80 answeredInRound
    ) public {
        roundIdMocked = roundId;
        answerMocked = answer;
        startedAtMocked = startedAt;
        updatedAtMocked = updatedAt;
        answeredInRoundMocked = answeredInRound;
    }

    // updates the conversion rate
    function setRate(uint256 _ethOut, uint256 _ilvIn) public {
        ethOut = _ethOut;
        ilvIn = _ilvIn;
    }

    /**
     * @inheritdoc AggregatorV3Interface
     */
    function decimals() public pure override returns (uint8) {
        return 18;
    }

    /**
     * @inheritdoc AggregatorV3Interface
     */
    function description() public pure override returns (string memory) {
        return "ILV / ETH (Mock!)";
    }

    /**
     * @inheritdoc AggregatorV3Interface
     */
    function version() public pure override returns (uint256) {
        return 0;
    }

    /**
     * @inheritdoc AggregatorV3Interface
     */
    function getRoundData(uint80 _roundId)
        public
        view
        override
        returns (
            uint80 roundId,
            int256 answer,
            uint256 startedAt,
            uint256 updatedAt,
            uint80 answeredInRound
        )
    {
        require(_roundId == roundIdMocked, "roundId differs from the roundId mocked value");
        return latestRoundData();
    }

    /**
     * @inheritdoc AggregatorV3Interface
     */
    function latestRoundData()
        public
        view
        override
        returns (
            uint80 roundId,
            int256 answer,
            uint256 startedAt,
            uint256 updatedAt,
            uint80 answeredInRound
        )
    {
        return (
            roundIdMocked,
            answerMocked >= 0 ? answerMocked : int256((10**decimals() * ethOut) / ilvIn),
            startedAtMocked < type(uint256).max ? startedAtMocked : now256(),
            updatedAtMocked < type(uint256).max ? updatedAtMocked : now256(),
            answeredInRoundMocked
        );
    }
}

contracts/PortraitLayer.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.14;

import "@openzeppelin/contracts-upgradeable/token/ERC721/IERC721Upgradeable.sol";
import "./BaseIlluvitar.sol";
import "./DataTypes.sol";

/**
 * @title Portrait Layer
 * @dev inherit BaseIlluvitar
 * @author Dmitry Yakovlevich
 */
contract PortraitLayer is BaseIlluvitar {
    /// @dev Portrait Metadata struct
    struct Metadata {
        uint8 tier; // tier
        // Bonded accessory token ids
        uint256 skinId; // bonded skin id
        uint256 bodyId; // bonded body id
        uint256 eyeId; // bonded eye wear id
        uint256 headId; // bonded head wear id
        uint256 propsId; // bonded props id
    }

    /// @dev Portrait metadata
    mapping(uint256 => Metadata) public metadata;

    /**
     * @dev Initialize Portrait Layer.
     * @param name_ NFT Name
     * @param symbol_ NFT Symbol
     * @param imxMinter_ IMX Minter Address
     */
    function initialize(
        string memory name_,
        string memory symbol_,
        address imxMinter_
    ) external initializer {
        __BaseIlluvitar_init(name_, symbol_, imxMinter_);
    }

    /**
     * @dev Mint Portrait with blueprint.
     * @dev blueprint has format of `a,b,c,d,e,f`
     *      a : tier
            b : bonded skin id
            c : bonded body id
            d : bonded eye wear id
            e : bonded head wear id
            f : bonded props id
     * @param to Recipient address
     * @param tokenId Token id
     * @param blueprint Portrait blueprint
     */
    function _mint(
        address to,
        uint256 tokenId,
        bytes memory blueprint
    ) internal override {
        _safeMint(to, tokenId);
        (
            uint8 tier,
            uint256 skinTokenId,
            uint256 bodyTokenId,
            uint256 eyeTokenId,
            uint256 headTokenId,
            uint256 propsTokenId
        ) = _parseBlueprint(blueprint);
        metadata[tokenId] = Metadata({
            tier: tier,
            skinId: skinTokenId,
            bodyId: bodyTokenId,
            eyeId: eyeTokenId,
            headId: headTokenId,
            propsId: propsTokenId
        });
        metadataInitialized[tokenId] = true;
    }

    /// @dev Parse blueprint
    function _parseBlueprint(bytes memory blueprint)
        private
        pure
        returns (
            uint8 tier,
            uint256 skinTokenId,
            uint256 bodyTokenId,
            uint256 eyeTokenId,
            uint256 headTokenId,
            uint256 propsTokenId
        )
    {
        uint256 len = blueprint.length;
        uint8 p;

        p = _skipNonDecimal(blueprint, p);
        require(_isDecimal(blueprint[p + 1]), "Wrong blueprint format");
        tier = uint8(blueprint[p++]) - 0x30;
        (skinTokenId, p) = _atoi(blueprint, p);
        (bodyTokenId, p) = _atoi(blueprint, p);
        (eyeTokenId, p) = _atoi(blueprint, p);
        (headTokenId, p) = _atoi(blueprint, p);
        (propsTokenId, p) = _atoi(blueprint, p);
        p = _skipNonDecimal(blueprint, p);
        require(p == len, "Wrong blueprint format");
    }

    /**
     * @dev Skip non-decimal characters and return the index of the first decimal
     *
     * @dev If no decimal character is present, it will return the length of `a`
     *
     * @param a numeric string to convert
     * @param offset an index to start parsing from, set to zero to parse from the beginning
     * @return p an index where the conversion stopped
     */
    function _skipNonDecimal(bytes memory a, uint8 offset) internal pure returns (uint8 p) {
        // skip wrong characters in the beginning of the string if any
        for (p = offset; p < a.length; p++) {
            // check if digit is valid and meets the base 10
            if (_isDecimal(a[p])) {
                // we've found decimal character, skipping stops
                return p;
            }
        }
        return p;
    }

    /**
     * @dev Simplified version of StringUtils.atoi to convert a bytes string
     *      to unsigned integer using ten as a base
     * @dev Stops on invalid input (wrong character for base ten) and returns
     *      the position within a string where the wrong character was encountered
     *
     * @dev Throws if input string contains a number bigger than uint256
     *
     * @param a numeric string to convert
     * @param offset an index to start parsing from, set to zero to parse from the beginning
     * @return i a number representing given string
     * @return p an index where the conversion stopped
     */
    function _atoi(bytes memory a, uint8 offset) internal pure returns (uint256 i, uint8 p) {
        // skip wrong characters in the beginning of the string if any
        p = _skipNonDecimal(a, offset);

        // iterate over the rest of the string (bytes buffer)
        for (; p < a.length; p++) {
            // check if digit is valid and meets the base 10
            if (!_isDecimal(a[p])) {
                // we've found bad character, parsing stops
                break;
            }

            // move to the next digit slot
            i *= 10;

            // extract the digit and add it to the result
            i += uint8(a[p]) - 0x30;
        }

        // return the result
        return (i, p);
    }
}

